    private void loginButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_loginButtonActionPerformed
        if (loginButton.isEnabled()) {

        }
        String u = getUsuario();
        String p = getPassword();
        Gestor g = new Gestor();
        if(g.getLogin().equals(u) && g.getSenha().equals(p)){
            new TelaGestor().setVisible(true);
            dispose();
        }
        else
        JOptionPane.showMessageDialog(this, "Usu√°rio ou senha incorretos.", "Erro de login", JOptionPane.ERROR_MESSAGE);
        limparCamposLogin();
    }//GEN-LAST:event_loginButtonActionPerformed

    private void cancelLoginActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cancelLoginActionPerformed
        TelaPrincipal telaPrincipal = new TelaPrincipal();
        telaPrincipal.setVisible(true);
        dispose();
    }//GEN-LAST:event_cancelLoginActionPerformed

